CREATE TABLE IF NOT EXISTS forma_pagamento(id bigint not null auto_increment,tipo varchar(30) not null,primary key(id));
CREATE TABLE IF NOT EXISTS pedido(id bigint not null auto_increment,comprador bigint not null,qtd bigint unsigned not null, data_compra date not null default (current_date()),data_entrega date not null,destino bigint not null,contato bigint,estatus enum('pedente','cancelado','aprovado','entregue','em rota') not null default 'pedente',forma_pagamento_id bigint not null,primary key(id),foreign key(comprador) references cliente(id),foreign key(destino) references endereco(id), foreign key(contato) references telefone(id), foreign key(forma_pagamento_id) references forma_pagamento(id));