CREATE TABLE IF NOT EXISTS endereco(id bigint not null auto_increment, estado varchar(20) not null,cidade varchar(30),bairro varchar(30) not null,rua varchar(40) not null,numero varchar(4)not null,complemento varchar(15),primary key(id));
CREATE TABLE IF NOT EXISTS telefone(id bigint not null auto_increment, dd varchar(2) not null, numero varchar(11) not null, primary key(id));
CREATE TABLE IF NOT EXISTS cliente(id bigint not null auto_increment, nome varchar(50) not null,idade date not null, sexo enum('masculino','feminino'),cpf varchar(11) not null unique,endereco_id bigint,telefone_id bigint,primary key(id), foreign key(endereco_id) references endereco(id) on delete cascade,foreign key(telefone_id) references telefone(id) on delete cascade);
CREATE TABLE IF NOT EXISTS genero(id bigint not null auto_increment, nome varchar(25) not null unique, primary key(id));
CREATE TABLE IF NOT EXISTS produtora(id bigint not null auto_increment, nome varchar(30) not null unique,aberto date default (current_date()), primary key(id));
CREATE TABLE IF NOT EXISTS jogo(id bigint not null auto_increment, nome varchar(40) not null, data_lanca date default (current_date()),qtd bigint unsigned not null default 0,preco decimal(6,2) unsigned not null default 0.0,produtora_id bigint not null, genero_id bigint not null,primary key(id), foreign key(produtora_id) references produtora(id), foreign key(genero_id) references genero(id));
